class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> prev;
        int n = nums.size();
        long long count = 0;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                count++;
            }
            curr[nums[i]] = 1;
            for (auto& [j, m] : prev) {
                long long newr = j & nums[i];
                if (newr == k) {
                    count += m;
                }
                curr[newr] += m;
            }
            prev = curr;
        }
        return count;
    }
};
