class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int sZZe = array.size();
        long long TtL = 0;
        unordered_map<long long, long long> PrS;
        for (int index = 0; index < sZZe; ++index) {
            unordered_map<long long, long long> CrS; 
            if (array[index] == target) {
                ++TtL;
            }
            CrS[array[index]] = 1;
            for (auto& [value, frequency] : PrS) {
                long long newAndResult = value & array[index];
                if (newAndResult == target) {
                    TtL += frequency;
                }
                CrS[newAndResult] += frequency;
            }

            PrS = CrS;
        }
        return TtL;
    }
};
