class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                count++;
            }
            curr[nums[i]] = 1;

            for (auto& it: prev) {
                long long temp = it.first& nums[i];
                if (temp == k) {
                    count += it.second;
                }
                curr[temp] += it.second;
            }

            prev = curr;
        }

        return count;
    }
};
