class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        vector<vector<int>>v(nums.size(), vector<int>(31,0));
        long long ans = 0;
        for(int i = 0; i<nums.size(); i++){
            for(int j = 0; j<=30; j++){
                if(((1<<j)&(nums[i]))){
                    v[i][j]++;
                    if(i)v[i][j]+=v[i-1][j];
                }
            }
        }
        if(!k){
            for(int i = 0; i<nums.size(); i++){
                int maxi = 0;
                for(int j = 0; j<=30; j++){
                    maxi = max(maxi,v[i][j]);
                }
                ans+=i-maxi+1;
            }
            return ans;
        }
        for(int i = 0; i<nums.size(); i++){
            int mink = i+1, maxnok = 0;
            for(int j = 0; j<=30; j++){
                if(((1<<j)&(k))){
                    mink = min(mink,v[i][j]);
                }else{
                    maxnok = max(maxnok,v[i][j]);
                }
            }
            // cout<<i<<" "<<mink<<" "<<maxnok<<endl;
            if(maxnok>=mink)continue;
            else{
                ans+=mink-maxnok;
            }
        }

        return ans;

    }
};
