class Solution {
public:
    void my_add(vector<int> &z, vector<int> &o, int n) {
        for (int i = 0; i < 32; i++) {
            if ((1 & (n >> i)) == 0) {
                z[i]++;
            } else {
                o[i]++;
            }
        }
    }
    void my_remove(vector<int> &z, vector<int> &o, int n) {
        for (int i = 0; i < 32; i++) {
            if ((1 & (n >> i)) == 0) {
                z[i]--;
            } else {
                o[i]--;
            }
        }
    }
    bool ok(vector<int> &z, vector<int> &o, int n) {
        int test = 0;
        for (int i = 0; i < 32; i++) {
            if (z[i] == 0) {
                test = test ^ (1 << i);
            }
        }
        return test <= n;
    }
    long long helper(vector<int> &v, int k) {
        long long ans = 0ll;
        int n = v.size();
        int l = 0;
        vector<int> z(32, 0), o(32, 0);
        for (int r = 0; r < n; r++) {
            my_add(z, o, v[r]);
            while (ok(z, o, k) && l <= r) {
                my_remove(z, o, v[l]);
                l++;
            }
            if (l <= r) {
                ans = ans + (long long)r - (long long)l + 1ll;
            }
        }
        return ans;
    }
    long long countSubarrays(vector<int>& v, int k) {
        return helper(v, k - 1) - helper(v, k);
    }
};
