#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& arr, int reach) {
        int n = arr.size();
        long long ans = 0;
        unordered_map<long long, long long> prv;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 
            if (arr[i] == reach) {
                ++ans;
            }
            curr[arr[i]] = 1;

            for (auto& [pv, pf] : prv) {
                long long res = pv & arr[i];
                if (res == reach) {
                    ans += pf;
                }
                curr[res] += pf;
            }

            prv = curr;
        }

        return ans;
    }
};
