class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n= nums.size();
        long long ans = 0;
        for (int i = 0; i < n; i++) {
            int j = i;
            while (j > 0 && (nums[j] & nums[j - 1]) != nums[j - 1]) {
                nums[j - 1] &= nums[j];
                j--;
            }
            auto it = equal_range(nums.begin(), nums.begin() + i + 1, k);
            if (it.first != nums.end()) {
                ans += (it.second - it.first);
            }
        }
        return ans;
    }
};
