class Solution {
public:

    long long countSubarrays(vector<int>& arr, int k) {
        long long ans = 0, n = arr.size();
    unordered_map<long long, long long> m1;


    for (long long i = 0; i < n; i++) {
        unordered_map<long long, long long> m2;

        for (auto &val : m1) {
            long long p = val.first & arr[i];
            m2[p] += val.second;
        }
        m2[arr[i]]++;
        m1 = m2;

        if (m1.find(k) != m1.end()) {
            ans += m1[k];
        }
    }

    return ans;
    }
};
