class Solution {
    public long countSubarrays(int[] nums, int k) {
       int size = nums.length;
        long total = 0;
        Map<Long, Long> previousResults = new HashMap<>(); 

        for(int index = 0 ; index < size ; index++){
            Map<Long , Long> curr = new HashMap<>();
            if(nums[index] == k){
                total++;
            }
            curr.put((long)nums[index] ,1L);
            for(Map.Entry<Long ,Long> entry : previousResults.entrySet()){
                long value = entry.getKey();
                long frequency = entry.getValue();
                long newAndResult = value & nums[index];

                if(newAndResult == k){
                    total += frequency;
                }
                curr.put(newAndResult , curr.getOrDefault(newAndResult, 0L) + frequency);

            }
             previousResults = curr;
        }
        return total;
    }
}
