class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> previousResults;

        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults;

            if (array[index] == target) {
                ++total;
            }

            currentResults[array[index]] = 1;

            for (const auto& [value, frequency] : previousResults) {
                long long newAndResult = value & array[index];
                if (newAndResult == target) {
                    total += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = move(currentResults);
        }

        return total;
    }
};
