class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=sz(nums);
        ll res=0;
        ll i=0;
        set<ll> st;
        ll ans=0;
        umapl dp;
        while(i<n){
            ll cur=nums[i];
            bool ch=0;
            if(cur-k==0) ch=1;
            else ch=0;
            if(ch) res++;
            umapl ndp;
            ndp[cur]++;
            st.insert(cur);
            for(auto it:dp){
                ll cur=it.first;
                ll check=cur&nums[i];
                if(st.find(check)!=st.end()) ans++;
                else ans=0;
                ndp[check]+=it.second;
                bool ch1=0;
                st.insert(check);
                if(check-k==0) ch1=1;
                else ch1=0;
                st.erase(cur);
                if(ch1) res+=it.second;
            }
            dp=ndp;
            i++;
        }
        rt(res);
    }
};
