class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans= 0;
        map<long long, long long> arr1;
        for (int i = 0; i <nums.size(); ++i) {
            map<long long, long long> arr2; 
            if (nums[i] == k)ans++;
            arr2[nums[i]] = 1;
            for (auto it : arr1) {
                long long curr = it.first & nums[i];
                if (curr == k) ans += it.second;
                arr2[curr] += it.second;
            }          
            arr1 = arr2;
        }      
        return ans;
    }
};
