class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<long long,long long> mp;
        long long ans=0;

        for(auto& i: nums){
            map<long long,long long> mp2;
            if(i==k){
                ans++;
            }
            mp2[i]++;
            //cout<<mp[i]<<" ";
            for(auto&p: mp){
                int first= p.first;
                int sec= p.second;
                //cout<<first;
                //cout<<sec;
                long long x= first & i;
                if(x==k){
                    ans+= sec;
                }

                mp2[x]+= sec;
            }

            //cout<<mp2.size();
            mp= mp2;
        }
        return ans;
    }
};
