class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();

        vector<vector<int>> pf(n, vector<int>(32));
        for(int i = 0; i < n; ++i){
            for(int j = 0; j < 32; ++j){
                pf[i][j] = ((v[i] >> j) & 1);
                if(i) pf[i][j] += pf[i - 1][j];
            }
        }

        auto sum = [&](int l, int r) -> int{
            int ans = 0;
            for(int j = 0; j < 32; ++j){
                int cnt = pf[r][j];
                if(l - 1 >= 0) cnt -= pf[l - 1][j];
                if(cnt == r - l + 1) ans |= (1 << j);
            }
            return ans;
        };

        long long ans = 0;

        for(int i = 0; i < n; ++i){
            int lo = 0, hi = i, mid, temp1 = -1, temp2 = i + 1;
            while(lo <= hi){
                mid = (lo + hi) / 2;

                if(sum(mid, i) >= k){
                    temp1 = mid;
                    hi = mid - 1;
                }else{
                    lo = mid + 1;
                }
            }

            if(temp1 == -1 || sum(temp1, i) != k) continue;

            lo = 0, hi = i;
            while(lo <= hi){
                mid = (lo + hi) / 2;

                if(sum(mid, i) >= k + 1){
                    temp2 = mid;
                    hi = mid - 1;
                }else{
                    lo = mid + 1;
                }
            }

            int LEN = temp2 - temp1;

            ans += LEN;
        }

        return ans;
    }
};
