class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        std::unordered_map<int, int> andCount;

        for (int num : nums) {
            std::unordered_map<int, int> newAndCount;

            for (const auto& entry : andCount) {
                int newAnd = entry.first & num;
                if (newAnd == k) {
                    count += entry.second;
                }
                newAndCount[newAnd] += entry.second;
            }

            newAndCount[num]++;
            if (num == k) {
                count++;
            }

            andCount = std::move(newAndCount);
        }

        return count;
    }
};
