class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        unordered_map<long long, long long>ump;
        for(int n : nums){
            if(n==k) ans++;
            unordered_map<long long, long long> ump1;
            ump1[n]=1;
            for(auto x : ump){
                long long a= x.first & n;
                if(a==k) ans+=x.second;
                ump1[a]+= x.second;
            }
            ump=ump1;
        }
        return ans;
    }
};
