class Solution {
    public long countSubarrays(int[] arr, int k) {
        long cnt = 0;
        Map<Integer, Long> map = new HashMap<>();
        for (int i = 0; i < arr.length; i++) {
            Map<Integer, Long> curr = new HashMap<>();
            if (arr[i] == k) 
                cnt++;
            curr.put(arr[i],1L);
            for (Map.Entry<Integer, Long> en:map.entrySet()) {
                int temp = en.getKey() & arr[i];
                if (temp == k) 
                    cnt+=en.getValue();
                curr.put(temp, curr.getOrDefault(temp, 0L) + en.getValue());
            }
            map=curr;

        }
        return cnt;
    }
}
