typedef long long int ll;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        ll cnt = 0;
        map<ll, ll> fin;
        for (int i= 0; i < n; ++i) {
            map<ll,ll> ck; 
            if (nums[i] == target)
                cnt++;
            ck[nums[i]] = 1;
            for (auto u : fin) {
                if ((u.first & nums[i]) == target) {
                    cnt += u.second;
                }
               ck[u.first & nums[i]] += u.second;
            }

            fin = ck;
        }
        return cnt;
    }
};
