class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        unordered_map<int,int> mp;
        for(int i=0;i<n;i++){
           unordered_map<int,int> temp;
            if(nums[i]==k)
            ans++;
            temp[nums[i]]=1;
            for(auto j:mp){
                int x=j.first & nums[i];
                if(x==k)
                ans+=j.second;
                 if(temp.find(x)!=temp.end())
                temp[x]+=j.second;
                else temp[x]=j.second;
            }
            mp=temp;
        }
        return ans;
    }
};
