class Solution {
public:
    struct Result
    {
        int v;
        int l;
        int r;
    };
    long long countSubarrays(vector<int>& nums, int k)
    {
        int n=nums.size();
        vector<int> ans(n);
        unordered_map<long long,long long> cnt;
        vector<Result> s;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<s.size();j++)
            {
                s[j].v&=nums[i];
            }
            s.push_back({nums[i],i,i+1});
            int j=0;
            for(int k=0;k<s.size();k++)
            {
                if(s[j].v!=s[k].v)
                    s[++j]=s[k];
                else
                    s[j].r=s[k].r;
            }
            s.resize(j+1);
            for(auto [val,l,r]:s)
                cnt[val]+=r-l;
        }
        return cnt[k];
    }
};
