class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        l = 0
        tl = 0
        n = len(nums)
        c = [0] * 32
        ans = 0
        for r in range(n):
            if (nums[r] & k) ^ k:
                l = r + 1
                tl = r + 1
                c = [0] * 32
                continue
            x = nums[r]
            for ind in range(32):
                c[ind] += 1 - ((x >> ind) & 1)
                ind += 1
            while tl < r:
                x = nums[tl]
                for ind in range(32):
                    if c[ind] == 1 and ((x >> ind) & 1) == 0:
                        break
                    ind += 1
                else:
                    x = nums[tl]
                    for ind in range(32):
                        c[ind] -= 1 - ((x >> ind) & 1)
                        ind += 1
                    tl += 1
                    continue
                break
            for i in range(32):
                if k & (1 << i) == 0 and c[i] == 0:
                    break
            else:
                ans += tl - l + 1
        return ans
