from collections import defaultdict

class Solution:
    def countSubarrays(self, array, target):
        size = len(array)
        total = 0
        previous_results = defaultdict(int)

        for index in range(size):
            current_results = defaultdict(int)

            if array[index] == target:
                total += 1

            current_results[array[index]] += 1

            for value, frequency in previous_results.items():
                new_and_result = value & array[index]

                if new_and_result == target:
                    total += frequency

                current_results[new_and_result] += frequency

            previous_results = current_results

        return total
