class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;
        vector<int> prev(32, -1);
        for(int i=0;i<nums.size();i++)
        {
            int maxy = -1, miny = i, f = 0;
            for(int j=0;j<32;j++)
            { 
                if((k & (1 << j)))
                {
                    if(nums[i] & (1 << j))
                    {
                        maxy = max(maxy, prev[j]);
                    }
                    else
                    { 
                        f = 1;
                        prev[j] = i;
                    }
                }
                else
                {
                    if(nums[i] & (1 << j))
                    {
                        if(prev[j] == -1)   
                        {
                            f = 1;
                        }
                        else
                        {
                            miny = min(miny, prev[j]);
                        }
                    }
                    else prev[j] = i;
                }
            }
            if(f)   continue;
            else    res += max(miny-maxy, 0);
            // cout << i << " " << res << " " << miny <<" " << maxy << endl;
        }
        return res;
    }
};
