class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map< long long, long long >and_map;
        long long res = 0;
        for( long long i = 0; i < n; i++ ){
            unordered_map< long long, long long >new_and_map;
            for( auto & it : and_map ){
                long long new_and = it.first & nums[i];
                new_and_map[new_and] += it.second;
            }

            new_and_map[nums[i]]++;
            and_map = new_and_map;

            if( and_map.find(k) != and_map.end() ){
                res += and_map[k];
            }
        }

        return res;
    }
};
