class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n=len(nums)
        count=0
        prev={}
        for i in range(n):
            curr={}
            if nums[i]==k:
                count+=1
            curr[nums[i]]=1
            for val,freq in prev.items():
                new_val=val & nums[i]
                if new_val==k:
                    count+=freq
                curr[new_val]=curr.get(new_val,0)+freq
            prev=curr
        return count
