class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> m;
        long long res = 0;

        for (int num : nums) {
            unordered_map<int, int> temp;
            temp[num] = 1;

            for (auto& [key, value] : m) {
                int new_key = key & num;
                temp[new_key] += value;
            }

            if (temp.find(k) != temp.end()) {
                res += temp[k];
            }

            m = std::move(temp);
        }

        return res;
    }
};
