class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<unordered_map<int, int>> dp(n + 1);
        long long ans = 0;
        for (int i = 0; i < n; ++i) {
            ++dp[i + 1][nums[i]];
            for (auto& [k, v] : dp[i]) {
                dp[i + 1][nums[i] & k] += v;
            }
            ans += dp[i + 1][k];
        }
        return ans;
    }
};
