class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();
        map<long long ,long long>mp1;
        for(int i=0;i<n;i++)
        {
            map<long long ,long long>mp2;
            if(nums[i]==k)
            {
                ans++;
            }
            mp2[nums[i]]=1;
            for(auto it:mp1)
            {
                long long t=it.first&nums[i];
                if(t==k)
                {
                    ans+=it.second;
                }
                mp2[t]+=it.second;
            }
            mp1=mp2;
        }
        return ans;
    }
};
