class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int array_size = array.size();
        long long x = 0;
        unordered_map<long long, long long> array_map;
        for (int i = 0; i < array_size; ++i) {
            unordered_map<long long, long long> count_map; 
            if (array[i] == target) {
                ++x;
            }
            count_map[array[i]] = 1;
            for (auto& [val, fre] : array_map) {
                long long final_result = val & array[i];
                if (final_result == target) {
                    x += fre;
                }
                count_map[final_result] += fre;
            }

            array_map = count_map;
        }
        return x;
    }
};
