class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n=v.size();
        int a[n][32];
        for(int i=0;i<n;i++){
            for(int j=0;j<31;j++) {
            if(v[i]&(1<<j)) {if(i) a[i][j]=a[i-1][j]+1; else a[i][j]=1;}
            else a[i][j]=0;
            }
        }
        long long ans=0;
        for(int i=0;i<n;i++){
            int kh=1e9, kdh=0, c=0;
            for(int j=0;j<31;j++){
                if(k&(1<<j)) {kh=min(kh, a[i][j]); c++;}
                else kdh=max(kdh, a[i][j]);
            }
            if(c==0) ans+=i+1-kdh;
            else if(kdh<=kh) ans+=kh-kdh;
        }
        return ans;
    }
};
