class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        int n = nums.size();
        unordered_map<int, int> andMap;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newMap;
            if (nums[i] == k) ++count;
            for (auto& [value, freq] : andMap) {
                int newAnd = value & nums[i];
                newMap[newAnd] += freq;
                if (newAnd == k) count += freq;
            }
            newMap[nums[i]]++;
            andMap = newMap;
        }

        return count;
    }
};
