#define ff first
#define ss second
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> dp, m;
        long long cnt = 0;
        for(int i = 0; i < nums.size(); ++i){
            dp.clear();
            for(auto v: m) dp[v.ff & nums[i]] += v.ss;
            dp[nums[i]]++;
            m = dp;
            // cout << dp[k] << endl;  
            cnt += dp[k];
        }
        return cnt;
    }
};
