class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size=nums.size();
        long long ans=0;
        unordered_map <long long,long long>mp;
        for(int i=0;i<size;i++){
                    unordered_map <long long,long long>mp2;
                    if(nums[i]==k)ans++;
                    mp2[nums[i]]=1;
                    for(auto &[val,freq]:mp){
                        long long hel = val&nums[i];
                        if(hel == k){
                            ans += freq;
                        }
                        mp2[hel] += freq;
                    }
                    mp=mp2;

        }
        return ans;
    }
};
