class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
      int n=nums.size();
      long long cnt=0;
      unordered_map<long long ,long long > pre;
      for(int i=0; i<n; i++){
        unordered_map<long long,long long >curr;
        if(nums[i] == k){
            cnt++;
        }
        curr[nums[i]] = 1;

        for(auto [val,fre] : pre){
            long long na=val & nums[i];
            if(na == k){
                cnt +=fre;
            }
            curr[na] +=fre;
        }
        pre = curr;
      } 
      return cnt; 
    }
};
