class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        first = defaultdict(int)

        for num in nums:
            last = defaultdict(int)

            if num == k:
                res += 1
            last[num] = 1

            for x, cnt in first.items():
                y = x & num
                if y == k:
                    res += cnt
                last[y] += cnt

            first = last

        return res
