class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long int ans=0;
        long long int n=nums.size();
        bool flag=true;
        unordered_map<long long int,long long int> m,p;
        int i=0;
        while(i<n){
            p.clear();
            if(nums[i]==k){
                ans++;
            }
            p[nums[i]]=1;
            for(auto j:m){
                long long val=j.first&nums[i];
                if(val==k){
                    ans+=j.second;
                }
                p[val]+=j.second;
            }
            m=p;
            i++;
        }
        return ans;
    }
};
