class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n = nums.size();
        long long ans= 0;
        map<long long, long long> prev;
        for (int i= 0; i < n; i++)
        {
            map<long long, long long> curr;
            if (nums[i] == k)
            {
                ans++;
            }
            curr[nums[i]] = 1;
            for (auto ii : prev)
            {
                long long ne =ii.first & nums[i];
                if (ne == k)
                {
                    ans += ii.second;
                }
                curr[ne] += ii.second;
            }

            prev = curr;
        }
        return ans;
    }
};
