class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count = defaultdict(int)
        count[nums[0]] += 1
        res = 0
        if k in count:
            res += count[k]
        for i in nums[1:]:
            temp = defaultdict(int)
            temp[i] += 1
            for j in count:
                temp[i&j] += count[j]
            count = copy.deepcopy(temp)
            if k in count:
                res += count[k]

        return res
