class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long c = 0;

        unordered_map<long long, long long> p;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> q;

            if (nums[i] == k) {
                ++c;
            }
            q[nums[i]] = 1;

            for (auto& [v, f] : p) {
                long long a = v & nums[i];
                if (a == k) {
                    c += f;
                }
                q[a] += f;
            }

            p = q;
        }

        return c;
    }
};
