import java.util.HashMap;
import java.util.Map;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        long count = 0;
        Map<Integer, Integer> ac = new HashMap<>();

        for (int num : nums) {
            Map<Integer, Integer> nac = new HashMap<>();

            for (Map.Entry<Integer, Integer> e : ac.entrySet()) {
                int na = e.getKey() & num;
                nac.put(na, nac.getOrDefault(na, 0) + e.getValue());
            }

            nac.put(num, nac.getOrDefault(num, 0) + 1);

            if (nac.containsKey(k)) {
                count += nac.get(k);
            }

            ac = nac;
        }

        return count;
    }
}
