class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt = 0;
        int n = nums.size();
        unordered_map<int, int> andCnt;

        for (int num : nums) {
            unordered_map<int, int> newAndCnt;
            if (num == k) cnt++; 
            newAndCnt[num]++;
            for (auto& [key, val] : andCnt) {
                int newKey = key & num;
                if (newKey == k) cnt += val;
                newAndCnt[newKey] += val;
            }
            andCnt = newAndCnt;
        }
        return cnt;
    }
};
