class Solution {
public:
    long long countSubarrays(vector<int>& numbers, int target) {
        int size = numbers.size();
        long long subarrayCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> currentResults;

            if (numbers[i] == target) {
                ++subarrayCount;
            }
            currentResults[numbers[i]] = 1;

            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & numbers[i];
                if (newAndResult == target) {
                    subarrayCount += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }

        return subarrayCount;
    }
};
