class Solution(object):
    def countSubarrays(self, nums, k):
        l = len(nums)
        sc = 0
        pr = {}
        for i in range(l):
            cr = {}
            if nums[i] == k:
                sc += 1
            cr[nums[i]] = cr.get(nums[i], 0) + 1
            for v, f in pr.items():
                nar = v & nums[i]
                if nar == k:
                    sc += f
                cr[nar] = cr.get(nar, 0) + f
            pr = cr
        return sc
