class Solution {
public:
    void solve(vector<int>& nums, int k,long long&ans,  map<long long ,long long >&m)
    {

        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            map<long long,long long>m1;
            if(nums[i]==k)
            {
                ans++;

            }
            m1[nums[i]]+=1;
            for(auto it:m)
            {
                long long val=it.first &nums[i];
                if(val==k)
                {
                    ans+=it.second;
                }
                m1[val]+=it.second;
            }

            m=m1;
          }
     }

 long long countSubarrays(vector<int>& nums, int k) {

        long long ans=0;
        map<long long ,long long >m;
        solve(nums,k,ans,m);
        return ans;
    }
};
