// # define long long ll;
class Solution {
public:
     long long countSubarrays(std::vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> pres;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cres;
            if (nums[i] == k) {
                count++;
            }
            cres[nums[i]] = 1;

            for (auto &[val, freq] : pres) {
                long long newres = val & nums[i];
                if (newres == k) {
                    count += freq;
                }
                cres[newres] += freq;
            }
            pres = cres;
        }
        return count;
    }

};
