class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);
        long long cnt = 0;       
        map<long long, long long> mp;
        for (int i = 0; i < nums.size(); i++) {
            map<long long, long long> arr; 
            if (nums[i] == k) {
                cnt = cnt + 1;
            }
            arr[nums[i]] = 1;
            for (auto& [val, it] : mp) {
                long long ind = (val & nums[i]);
                if (ind == k) {
                    cnt = cnt + it;
                }
                arr[ind] = arr[ind] + it;
            }       
            mp = arr;
        }
        return cnt;
    }
};
