class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ands = {} # 以i为r的所有可能
        cnt = 0
        for x in nums:
            t = {}
            # ands = {a & x for a in ands}
            for a in ands.keys():
                t[a & x] = ands[a] + t.get(a & x,0)
            t[x] = t.get(x,0) + 1
            ands = t
            cnt += t.get(k,0)
        return cnt
