class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long tot = 0;
        unordered_map<long long, long long> cnt;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> res; 
            if (arr[i] == k) {
                ++tot;
            }
            res[arr[i]] = 1;
            for (auto& [value, freq] : cnt) {
                long long temp = value & arr[i];
                if (temp == k) {
                    tot += freq;
                }
                res[temp] += freq;
            }

            cnt = res;
        }
        return tot;
    }
};
