     long long countSubarrays(vector<int>&nums, int k) {
        int n=nums.size();
        long long ans = 0;
        int i=0;
        unordered_map<long long, int> temp;
        while(i<n) {
            unordered_map<long long, int> bits; 
            bits[nums[i]] = 1;
            if (nums[i]==k) {
                ++ans;
            }
            for (auto& [j, freq] : temp) {
                long long mask = j & nums[i];
                if (mask==k) {
                    ans+=freq;
                }
                bits[mask]+=freq;
            }
            temp=bits;
            i++;
        }
        return ans;
    }
};
