import java.util.HashMap;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        long ans=0;
        int n=nums.length;
        HashMap<Integer, Integer>mp=new HashMap<>();

        for (int i :nums) {
            HashMap<Integer,Integer>nx=new HashMap<>();

            for (var e : mp.entrySet()) {
                nx.merge(e.getKey()&i, e.getValue(), Integer::sum);
            }
            nx.merge(i, 1, Integer::sum);
            mp=nx;

            ans+=nx.getOrDefault(k,0);
        }
        return ans;
    }
}
